member.example:
This sample shows how to set the Title for the chart

void MyPage()
{
	SfChart chart = new SfChart();
	chart.Title = new ChartTitle(){ Text = "My Header" };
}

This sample code shows how to set the ColorModel property of the chart. 

void MyPage()
{
	SfChart chart = new SfChart();
	chart.ColorModel.Palette = ChartColorPalette.Metro;

	this.content = chart;
}

This sample code shows how to set title for the chart axis.

 void MyPage()
{
	SfChart chart = new SfChart();

	ChartAxisTitle axisTitle = new ChartAxisTitle ();
	axisTitle.Text = "Primary Axis";
	axisTitle.TextColor = Color.Red;

	chart.PrimaryAxis.Title = axisTitle;
}

This below code shows how to set the MajorGridLineStyle.

void MyPage()
{
	SfChart chart = new SfChart();

	ChartLineStyle majorGridLineStyle = new ChartLineStyle ();
	majorGridLineStyle.StrokeColor = Color.Gray;
	majorGridLineStyle.StrokeDashArray = new double[]{ 1, 3 };
	chart.PrimaryAxis.MajorGridLineStyle = majorGridLineStyle;
}

This below code shows how to set the MajorTickStyle.

void MyPage()
{
	SfChart chart = new SfChart();

	ChartAxisTickStyle tickStyle = new ChartAxisTickStyle ();
	tickStyle.TickSize = 1.5;
	tickStyle.TickPosition = AxisElementPosition.Inside;

	chart.PrimaryAxis.MajorTickStyle = tickStyle;
}
