    {
        public GaugePosition()
        {
            InitializeComponent();
            SfCircularGauge gauge = new SfCircularGauge();
            CircularScale scale = new CircularScale();
            scale.StartAngle = 180;
            scale.SweepAngle = 180;
            scale.SweepDirection = SweepDirection.Clockwise;
